            <script type="text/javascript" src="assets/script/jquery.SuperSlide.2.1.1.js"></script>
    		<script type="text/javascript" src="assets/script/magnifying.js"></script>
    		<script type="text/javascript">
    			$(document).ready(function(){
                    //图片放大引用
                    $('.jqzoom').jqzoom({
                        zoomType: 'standard',
                        lens:true,
                        preloadImages: false,
                        alwaysOn:false
                    });

    				//tab切换
    				$(".top-list-tab li").mouseover(function(){
    					var _this=$(this),
    						idx=_this.index(),
    						con=_this.parent().siblings(".panels").find(".panel");
    					console.log(idx);
    					_this.addClass("selected").siblings().removeClass("selected");
    					con.eq(idx).removeClass("disappear").siblings(".panel").addClass("disappear");
    				});
    				
    				//宝贝分类
    				$(".cats-tree .fst-cat-icon").toggle(function(){
    					var _this=$(this),
    						box=_this.parents(".cats-tree"),
    						fst_hd=_this.parent("h4"),
    						con=fst_hd.next("ul");
    					box.find(".fst-cat-icon").removeClass("ks-switchable-select");
    					_this.addClass("ks-switchable-select");
    					_this.removeClass("active-trigger");
    					con.hide();
    				},function(){
    					var _this=$(this),
    					fst_hd=_this.parent("h4"),
    					con=fst_hd.next("ul");
    					_this.addClass("active-trigger");
    					con.show();
    				});
    				
    				//幻灯片
    				jQuery(".slide-box").slide({mainCell:".slide-content",effect:"left",autoPlay:true});
    				
    				//所有商品
    				$(".all-cats-popup .cats-tree li").hover(function(){
    					var _this=$(this),
    						offx=_this.offset().left,
    						offy=_this.offset().top;
    					$(this).find(".snd-pop").css({"left":188,"top":0}).show();
    				},function(){
    					$(this).find(".snd-pop").css({"left":9999,"top":9999}).hide();
    				});
    				
    				//导航
    				$(".tshop-pbsm-shop-nav-ch .popup-container").hover(function(){
    					var _this=$(this),
    						offx=_this.offset().left,
    						offy=_this.offset().top,
    						idx=_this.index();
    					$(".tshop-pbsm-shop-nav-ch .popup-content").eq(idx).css({"left":offx,"top":offy+30,"z-index":201}).show();
    				},function(){
    					$(".tshop-pbsm-shop-nav-ch .popup-content").hide();
    				})
    				$(".tshop-pbsm-shop-nav-ch .popup-content").hover(function(){
    					$(this).show();
    				},function(){
    					$(this).hide();
    				});
    				
    				
    				//更多选项
    				$(".tshop-pbsm-tmall-srch-list .attrExtra-more").toggle(function(){
    					$(this).text("精简选项").parents(".attrs").find(".J_TMoreAttrsCont").addClass("attrExtra-more-drop").show();
    				},function(){
    					$(this).text("更多选项").parents(".attrs").find(".J_TMoreAttrsCont").removeClass("attrExtra-more-drop").hide();
    				})


                    //数量控制
                    $(".mui-amount-increase").click(function(){
                        var a=$(".mui-amount-input").val();
                        a++;
                        $(".mui-amount-input").val(a);
                        if($(".mui-amount-input").val() == 1){
                            $(".mui-amount-decrease").addClass("first");
                        }else{
                            $(".mui-amount-decrease").removeClass("first");
                        }
                    });
                    
                    $(".mui-amount-decrease").click(function(){
                        var a=$(".mui-amount-input").val();
                        if(a>1){
                            a--;
                        }
                        $(".mui-amount-input").val(a);
                        if($(".mui-amount-input").val() == 1){
                            $(".mui-amount-decrease").addClass("first");
                        }else{
                            $(".mui-amount-decrease").removeClass("first");
                        }
                    });


                    //选项卡切换
                    $(".itagBox .itagTit li").mouseover(function(){
                        var ind=$(this).index();
                            _this=$(this);
                        _this.addClass("current").siblings().removeClass("current");
                        _this.parents(".itagBox").find(".itagList").eq(ind).show().siblings().hide();   
                    });
    

                    //更多优惠显示
                    $(".tm-shopPromotion-title .more").click(function() {
                        $(this).parent(".tm-shopPromotion-title").hide().next(".tm-floater-Box").removeClass('hidden');
                    });
                    $(".tm-floater-Box .floater .unmore").click(function() {
                        $(this).parents(".tm-floater-Box").addClass('hidden').prev('.tm-shopPromotion-title').show();
                    });

                    //手机购买二维码
                    $(".tm-qrcode-icon").hover(function(){
                        setTimeout("$('.tm-qrcode-pic').show();",300)
                    },function(){
                        setTimeout("$('.tm-qrcode-pic').hide();",300)
                    })
    				
    			})
    				
    		</script>
    		
    		
    		
    		
    		
    		
    	
    		
    		